import React, { useState, useEffect } from 'react';
import './App.css';

const webSocket = new WebSocket('ws://127.0.0.1:8080/chat');

export const ChatWindow = () => {
  const [message, setMessage] = useState('');
  const [receivedMessage, setReceivedMessage] = useState([]);

  useEffect(() => {
    setWebSocket();

    webSocket.onmessage = (event) => {
      console.log('received message', event);
      const messages = receivedMessage;
      messages.push(event.data);

      setReceivedMessage(messages);
    }
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    webSocket.send(message);
    setMessage('');
  }

  const handleChange = event => setMessage(event.target.value);

  return (
    <div>
      <div id='messageArea'>
        {console.log(Array.isArray(receivedMessage))}
        {receivedMessage.map( (element) => {
          return (
            <div>
              {element}
            </div>
          )
        })}
      </div>
      <div>
        <form onSubmit={handleSubmit}>
          <input type='text' name='messageInput' onChange={handleChange} />
        </form>
      </div>
    </div>
  )
};
